// src/modal/EditInventoryModal.js
import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { updateInventoryItem } from '../layout/inventoryService'; // Ensure this service method is implemented
//import './EditInventoryModal.css';

Modal.setAppElement('#root'); // For accessibility

const EditInventoryModal = ({ isOpen, onRequestClose, item, onSave }) => {
  const [formData, setFormData] = useState(null);

  useEffect(() => {
    setFormData(item);
  }, [item]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = () => {
    updateInventoryItem(formData.id, formData)
      .then(() => {
        onSave(formData);
        onRequestClose();
      })
      .catch(error => console.error('Error updating inventory item:', error));
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Edit Inventory Item"
      className="modal"
      overlayClassName="overlay"
    >
      <div className="modal-header">
        <h2>Edit Inventory Item</h2>
      </div>
      <form>
        <label>
          Name:
          <input type="text" name="name" value={formData.name || ''} onChange={handleChange} />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={formData.category || ''} onChange={handleChange} />
        </label>
        <label>
          Unit Price:
          <input type="number" name="unit_price" value={formData.unit_price || ''} onChange={handleChange} />
        </label>
        <label>
          Stock Level:
          <input type="number" name="stock_level" value={formData.stock_level || ''} onChange={handleChange} />
        </label>
        <label>
          Reorder Level:
          <input type="number" name="reorder_level" value={formData.reorder_level || ''} onChange={handleChange} />
        </label>
        <button type="button" onClick={handleSubmit}>Save</button>
        <button type="button" className="close-button" onClick={onRequestClose}>Close</button>
      </form>
    </Modal>
  );
};

export default EditInventoryModal;
