// src/InventoryModal.js

import React, { useState } from 'react';
import Modal from 'react-modal';
import './InventoryModal.css'; // Create a CSS file for modal styling

Modal.setAppElement('#root'); // For accessibility

const InventoryModal = ({ isOpen, onRequestClose, onSave }) => {
  const [productName, setProductName] = useState('');
  const [category, setCategory] = useState('Raw Material');
  const [unitPrice, setUnitPrice] = useState('');
  const [reorderLevel, setReorderLevel] = useState('');

  const handleSave = () => {
    const newItem = {
      name: productName,
      category,
      unit_price: parseFloat(unitPrice),
      reorder_level: parseInt(reorderLevel, 10),
    };
    onSave(newItem);
    handleClose();
  };

  const handleClose = () => {
    // Reset form fields
    setProductName('');
    setCategory('Raw Material');
    setUnitPrice('');
    setReorderLevel('');
    onRequestClose();
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={handleClose}
      contentLabel="Add Inventory Item"
      className="modal"
      overlayClassName="overlay"
    >
      <div className="modal-header">
        <h2>Add Inventory Item</h2>

      </div>
      <form>
        <label>
          Product Name:
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            required
          />
        </label>
        <label>
          Category:
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="Raw Material">Raw Material</option>
            <option value="Finished Product">Finished Product</option>
            {/* Add more categories if needed */}
          </select>
        </label>
        <label>
          Unit Price:
          <input
            type="number"
            value={unitPrice}
            onChange={(e) => setUnitPrice(e.target.value)}
            required
          />
        </label>
        <label>
          Reorder Level:
          <input
            type="number"
            value={reorderLevel}
            onChange={(e) => setReorderLevel(e.target.value)}
            required
          />
        </label>
        <button type="button" onClick={handleSave}>Save</button>
        <button type="button" className="cancel-button" onClick={handleClose}>Close</button>
      </form>
    </Modal>
  );
};

export default InventoryModal;
