/* src/production_layout/production_list/ProductionList.js */

import React, { useState } from 'react';
import AddProductionModal from '../../productionModal/AddProductionModal'; // Import the AddProductionModal component
import './ProductionList.css'; // Ensure this path is correct

const ProductionList = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false); // State to manage the modal open/close
  const [productions, setProductions] = useState([]); // State to manage production entries

  const handleOpenModal = () => {
    setModalIsOpen(true);
  };

  const handleCloseModal = () => {
    setModalIsOpen(false);
  };

  const handleAddProduction = (productName) => {
    // Update productions state with the new product name
    setProductions([...productions, productName]);
    handleCloseModal(); // Close the modal after adding the production
  };

  return (
    <div>
      <h2>Production List</h2>
      <button onClick={handleOpenModal}>Add New Production</button>
      <AddProductionModal
        isOpen={modalIsOpen}
        onRequestClose={handleCloseModal}
        onAddProduction={handleAddProduction} // Pass the function to handle adding production
      />
      <div className="production-list">
  {productions.map((product, index) => (
    <div key={index} className="production-item">
      <h3>Product:</h3>
      <p>{product}</p>
      <button type="button">Select Raw Materials</button>
      <button type="button">Record Production</button>
    </div>
  ))}
</div>

    </div>
  );
};

export default ProductionList;
