/* src/production_layout/production_list/ProductionList.js */

// src/production_layout/production_list/ProductionList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddProductionModal from '../../productionModal/AddProductionModal'; // Import the AddProductionModal component
import './ProductionList.css'; // Ensure this path is correct

const ProductionList = () => {
  const [productions, setProductions] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false); // State to manage the modal open/close

  useEffect(() => {
    axios.get('/api/production/records/')
      .then(response => {
        setProductions(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the production records!', error);
      });
  }, []);

  const handleOpenModal = () => {
    setModalIsOpen(true);
  };

  const handleCloseModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div>
      <h2>Production List</h2>
      <button onClick={handleOpenModal}>Add New Production</button>
      <AddProductionModal
        isOpen={modalIsOpen}
        onRequestClose={handleCloseModal}
      />
      {/* Render the production list */}

    </div>
  );
};

export default ProductionList;
