/* src/production_layout/production_list/ProductionList.js */

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ProductionList = () => {
  const [productions, setProductions] = useState([]);

  useEffect(() => {
    axios.get('/api/production/records/')
      .then(response => {
        setProductions(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the production records!', error);
      });
  }, []);

  return (
    <div>
      <h2>Production List</h2>
      <Link to="/production-list/add">
        <button>Add New Production</button>
      </Link>
      <table>
        <thead>
          <tr>
            <th>Production ID</th>
            <th>Product</th>
            <th>Raw Material IDs</th>
            <th>Quantity Produced</th>
            <th>Quantity Used</th>
            <th>Production Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {productions.map(production => (
            <tr key={production.id}>
              <td>{production.id}</td>
              <td>{production.product}</td>
              <td>{production.raw_materials.map(material => material.raw_material.id).join(', ')}</td>
              <td>{production.quantity_produced}</td>
              <td>{production.raw_materials.reduce((sum, material) => sum + material.quantity_used, 0)}</td>
              <td>{production.production_date}</td>
              <td>
                <Link to={`/production-list/${production.id}`}>
                  <button>View</button>
                </Link>
                <Link to={`/production-list/edit/${production.id}`}>
                  <button>Edit</button>
                </Link>
                {/* Add a delete button if necessary */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductionList;
