/* src/production_layout/production_list/ProductionList.js */

import React, { useState } from 'react';
import './ProductionList.css'; // Ensure this path is correct

const ProductionList = () => {
  const [productName, setProductName] = useState('');
  const [isProductEntered, setIsProductEntered] = useState(false);

  const handleProductEnter = () => {
    setIsProductEntered(true);
  };

  const handleSelectRawMaterials = () => {
    // Implement logic to handle raw materials selection
    console.log('Select raw materials for:', productName);
  };

  const handleRecordProduction = () => {
    // Implement logic to record the production
    console.log('Record production for:', productName);
  };

  return (
    <div>
      <h2>Production List</h2>
      {!isProductEntered ? (
        <div className="input-container">
          <label>
            Production Name:
            <input
              type="text"
              value={productName}
              onChange={(e) => setProductName(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  handleProductEnter();
                }
              }}
            />
          </label>
          <button type="button" onClick={handleProductEnter}>Enter</button>
        </div>
      ) : (
        <div className="production-details">
          <div className="product-container">
            <h3>Product:</h3>
            <p>{productName}</p>
          </div>
          <div className="actions-container">
            <button type="button" onClick={handleSelectRawMaterials}>
              Select Raw Materials
            </button>
            <button type="button" onClick={handleRecordProduction}>
              Record Production
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductionList;
