/* src/layout/dashboard/Dashboard.js */

import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [stockLevels, setStockLevels] = useState([]);
  const [pieChartData, setPieChartData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/historical-data/');
        const fetchedData = response.data;

        // Aggregate data by date
        const aggregatedData = {};
        fetchedData.forEach(item => {
          const { date, product, quantity } = item;
          if (!aggregatedData[date]) {
            aggregatedData[date] = {};
          }
          if (!aggregatedData[date][product]) {
            aggregatedData[date][product] = 0;
          }
          aggregatedData[date][product] += quantity;
        });

        // Convert aggregated data to array format for the line chart
        const formattedData = Object.entries(aggregatedData).map(([date, products]) => ({
          date,
          total_quantity: Object.values(products).reduce((a, b) => a + b, 0),
        }));

        setData(formattedData);

        // Convert aggregated data to array format for the stock levels table
        const formattedStockLevels = Object.entries(aggregatedData).flatMap(([date, products]) =>
          Object.entries(products).map(([product, quantity]) => ({
            date,
            product,
            quantity,
          }))
        );

        setStockLevels(formattedStockLevels);

        // Prepare data for pie charts
        const pieData = Object.entries(aggregatedData).map(([date, products]) => ({
          date,
          total_quantity: Object.values(products).reduce((a, b) => a + b, 0),
        }));
        setPieChartData(pieData);
      } catch (error) {
        console.error('Error fetching historical data:', error);
      }
    };

    fetchData();
  }, []);

  // Custom tooltip content for line chart
  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const { date, total_quantity } = payload[0].payload;
      return (
        <div className="custom-tooltip">
          <p>{`Date: ${date}`}</p>
          <p>{`Total Quantity: ${total_quantity}`}</p>
        </div>
      );
    }
    return null;
  };

  // Pie chart colors
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#FF6347'];

  return (
    <div className="dashboard">
      <h2>Inventory Trends Over Time</h2>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip content={<CustomTooltip />} />
            <Legend />
            <Line type="monotone" dataKey="total_quantity" stroke="#8884d8" />
          </LineChart>
        </ResponsiveContainer>
      </div>

      <div className="stock-levels-container">
        <h2>Stock Levels</h2>
        <table className="stock-levels-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Product</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {stockLevels.map((item, index) => (
              <tr key={index}>
                <td>{item.date}</td>
                <td>{item.product}</td>
                <td>{item.quantity}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="pie-charts-container">
        {pieChartData.length > 0 && (
          <ResponsiveContainer width="100%" height={400}>
            <PieChart>
              <Pie
                data={pieChartData}
                dataKey="total_quantity"
                nameKey="date"
                cx="50%"
                cy="50%"
                outerRadius={150}
                fill="#8884d8"
                label
              >
                {pieChartData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
            </PieChart>
          </ResponsiveContainer>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
