// src/production_layout/productionModal/RawMaterialModal.js

import React, { useState } from 'react';
import Modal from 'react-modal';
import './RawMaterialModal.css'; // Ensure this path is correct

Modal.setAppElement('#root'); // For accessibility

const RawMaterialModal = ({ isOpen, onRequestClose, rawMaterials, productName, onAddRawMaterial }) => {
  const [selectedMaterial, setSelectedMaterial] = useState('');
  const [quantity, setQuantity] = useState('');

  const handleAddRawMaterial = () => {
    onAddRawMaterial(productName, selectedMaterial, quantity);
    setSelectedMaterial('');
    setQuantity('');
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Select Raw Materials"
      className="modal"
      overlayClassName="overlay"
    >
      <div className="modal-header">
        <h2>Select Raw Materials for {productName}</h2>
      </div>
      <form>
        <label>
          Raw Material:
          <select
            value={selectedMaterial}
            onChange={(e) => setSelectedMaterial(e.target.value)}
          >
            <option value="">Select a raw material</option>
            {rawMaterials.map(rm => (
              <option key={rm.id} value={rm.name}>{rm.name}</option>
            ))}
          </select>
        </label>
        <label>
          Quantity:
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
          />
        </label>
        <button type="button" onClick={handleAddRawMaterial}>Add Raw Material</button>
        <button type="button" className="cancel-button" onClick={onRequestClose}>Close</button>
      </form>
    </Modal>
  );
};

export default RawMaterialModal;
