// src/production_layout/productionModal/AddEditProductionForm.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';  // Ensure axios is installed
import './AddEditProductionForm.css';

const AddEditProductionForm = ({ production, onSave, onCancel }) => {
  const [productName, setProductName] = useState(production ? production.productName : '');
  const [selectedRawMaterials, setSelectedRawMaterials] = useState(production ? production.rawMaterialNames : []);
  const [quantityProduced, setQuantityProduced] = useState(production ? production.quantityProduced : '');
  const [quantityUsed, setQuantityUsed] = useState(production ? production.quantityUsed : '');
  const [inventoryItems, setInventoryItems] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/api/inventory/')
      .then(response => {
        setInventoryItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching inventory items:', error);
      });

    if (production) {
      setProductName(production.productName);
      setSelectedRawMaterials(production.rawMaterialNames);
      setQuantityProduced(production.quantityProduced);
      setQuantityUsed(production.quantityUsed);
    }
  }, [production]);

  const handleSave = () => {
    const newProduction = {
      id: production ? production.id : null,
      productName,
      rawMaterialNames: selectedRawMaterials, // Update the property name to match the backend
      quantityProduced,
      quantityUsed
    };
    onSave(newProduction);
  };

  const handleRawMaterialChange = (e) => {
    const options = e.target.options;
    const selected = [];
    for (let i = 0; i < options.length; i++) {
      if (options[i].selected) {
        selected.push(options[i].value);
      }
    }
    setSelectedRawMaterials(selected);
  };

  return (
    <div className="modal-overlay">
      <div className="form-container">
        <h2>{production ? 'Edit Production Record' : 'Add New Production Record'}</h2>
        <div className="form-group">
          <label>Product Name:</label>
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Enter Product Name"
          />
        </div>
        <div className="form-group">
          <label>Raw Material Name:</label>
          <select
            multiple
            value={selectedRawMaterials}
            onChange={handleRawMaterialChange}
          >
            <option value="">Select Raw Materials</option>
            {inventoryItems.map(item => (
              <option key={item.id} value={item.name}>
                {item.name}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Quantity Produced:</label>
          <input
            type="number"
            value={quantityProduced}
            onChange={(e) => setQuantityProduced(e.target.value)}
            placeholder="Enter Quantity Produced"
          />
        </div>
        <div className="form-group">
          <label>Quantity Used:</label>
          <input
            type="number"
            value={quantityUsed}
            onChange={(e) => setQuantityUsed(e.target.value)}
            placeholder="Enter Quantity Used"
          />
        </div>
        <div className="form-buttons">
          <button onClick={handleSave}>{production ? 'Update' : 'Save'}</button>
          <button onClick={onCancel}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

export default AddEditProductionForm;
