// src/InventoryList.js
import React, { useState, useEffect } from 'react';
import { getInventoryItems, addInventoryItem, recordTransaction, deleteInventoryItem  } from './inventoryService';
import InventoryModal from '../modal/InventoryModal';
import './InventoryList.css';

const InventoryList = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [inventory, setInventory] = useState([]);

  useEffect(() => {
    // Fetch inventory items from Django
    getInventoryItems()
      .then(data => setInventory(data))
      .catch(error => console.error('Error fetching inventory:', error));
  }, []);

  const handleOpenModal = () => {
    setModalIsOpen(true);
  };

  const handleCloseModal = () => {
    setModalIsOpen(false);
  };

  const handleSave = (item) => {
    addInventoryItem(item)
      .then(data => {
        setInventory([...inventory, data]);
        // Record the transaction
        recordTransaction({
          product: data.id,
          transaction_type: 'Addition',
          quantity: item.reorder_level, // Use reorder_level as quantity
          remarks: 'Item added'
        })
        .catch(error => console.error('Error recording transaction:', error));
        setModalIsOpen(false);
      })
      .catch(error => console.error('Error adding inventory item:', error));
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      try {
        await deleteInventoryItem(id);
        setInventory(inventory.filter(item => item.id !== id));
      } catch (error) {
        console.error('Error deleting inventory item:', error);
      }
    }
  };

  return (
    <div className="inventory-list">
      <h2>Inventory List</h2>
      <button onClick={handleOpenModal}>Add Item</button>
      <InventoryModal
        isOpen={modalIsOpen}
        onRequestClose={handleCloseModal}
        onSave={handleSave}
      />
      <table>
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Product Name</th>
            <th>Category</th>
            <th>Unit Price</th>
            <th>Stock Level</th>
            <th>Reorder Level</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
        {inventory.map((item) => {
  const unitPrice = parseFloat(item.unit_price); // Convert unit_price to a number
  return (
    <tr key={item.id}> {/* Ensure the key is unique */}
      <td>{item.id}</td>
      <td>{item.name}</td>
      <td>{item.category}</td>
      <td>${unitPrice ? unitPrice.toFixed(2) : 'N/A'}</td>
      <td>{item.stock_level}</td>
      <td>{item.reorder_level}</td>
      <td>
        <button className="actions-button">View</button>
        <button className="actions-button">Edit</button>
        <button className="actions-button" onClick={() => handleDelete(item.id)}>Delete</button>
      </td>
    </tr>
  );
})}
        </tbody>
      </table>
    </div>
  );
};

export default InventoryList;
