// src/production_layout/productionModal/AddProductionModal.js

import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import axios from 'axios';
import './AddProductionModal.css'; // Ensure this path is correct

Modal.setAppElement('#root'); // For accessibility

const AddProductionModal = ({ isOpen, onRequestClose }) => {
  const [products, setProducts] = useState([]);
  const [rawMaterials, setRawMaterials] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [selectedMaterials, setSelectedMaterials] = useState([]);
  const [quantityProduced, setQuantityProduced] = useState('');
  const [productionDate, setProductionDate] = useState('');
  const [remarks, setRemarks] = useState('');

  useEffect(() => {
    // Fetch raw materials
    axios.get('/api/inventory/')
      .then(response => {
        setRawMaterials(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching raw materials!', error);
      });
  }, []);

  const handleAddProduction = () => {
    const productionData = {
      product: selectedProduct,
      raw_materials: selectedMaterials,
      quantity_produced: quantityProduced,
      production_date: productionDate,
      remarks
    };

    axios.post('/api/production/records/', productionData)
      .then(() => {
        onRequestClose(); // Close the modal on success
      })
      .catch(error => {
        console.error('There was an error adding the production record!', error);
      });
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Add Production"
      className="modal"
      overlayClassName="overlay"
    >
      <div className="modal-header">
        <h2>Add Production</h2>
      </div>
      <form>
        <label>
          Product:
          <input type="text" value={selectedProduct} onChange={(e) => setSelectedProduct(e.target.value)} />
        </label>
        <label>
          Raw Materials:
          <select multiple onChange={(e) => setSelectedMaterials(Array.from(e.target.selectedOptions, option => option.value))}>
            {rawMaterials.map(material => (
              <option key={material.id} value={material.id}>{material.name}</option>
            ))}
          </select>
        </label>
        <label>
          Quantity Produced:
          <input type="number" value={quantityProduced} onChange={(e) => setQuantityProduced(e.target.value)} />
        </label>
        <label>
          Production Date:
          <input type="date" value={productionDate} onChange={(e) => setProductionDate(e.target.value)} />
        </label>
        <label>
          Remarks:
          <textarea value={remarks} onChange={(e) => setRemarks(e.target.value)} />
        </label>
        <div className="button-group">
          <button type="button" onClick={handleAddProduction}>Add Production</button>
          <button type="button" className="cancel-button" onClick={onRequestClose}>Close</button>
        </div>
      </form>
    </Modal>
  );
};

export default AddProductionModal;
