// src/production_layout/productionModal/AddEditProductionForm.js

import React, { useState } from 'react';
import './AddEditProductionForm.css';

const AddEditProductionForm = ({ production, onSave, onCancel }) => {
  const [productID, setProductID] = useState(production ? production.productID : '');
  const [rawMaterialID, setRawMaterialID] = useState(production ? production.rawMaterialID : '');
  const [quantityProduced, setQuantityProduced] = useState(production ? production.quantityProduced : '');
  const [quantityUsed, setQuantityUsed] = useState(production ? production.quantityUsed : '');
  const [productionDate, setProductionDate] = useState(production ? production.productionDate : '');

  const handleSave = () => {
    const newProduction = {
      productionID: production ? production.productionID : Date.now(), // Auto-generate ID if not editing
      productID,
      rawMaterialID,
      quantityProduced,
      quantityUsed,
      productionDate
    };
    onSave(newProduction);
  };

  return (
    <div className="form-container">
      <h2>{production ? 'Edit Production Record' : 'Add New Production Record'}</h2>
      <div className="form-group">
        <label>Product ID:</label>
        <input
          type="text"
          value={productID}
          onChange={(e) => setProductID(e.target.value)}
          placeholder="Enter Product ID"
        />
      </div>
      <div className="form-group">
        <label>Raw Material ID:</label>
        <input
          type="text"
          value={rawMaterialID}
          onChange={(e) => setRawMaterialID(e.target.value)}
          placeholder="Enter Raw Material ID"
        />
      </div>
      <div className="form-group">
        <label>Quantity Produced:</label>
        <input
          type="number"
          value={quantityProduced}
          onChange={(e) => setQuantityProduced(e.target.value)}
          placeholder="Enter Quantity Produced"
        />
      </div>
      <div className="form-group">
        <label>Quantity Used:</label>
        <input
          type="number"
          value={quantityUsed}
          onChange={(e) => setQuantityUsed(e.target.value)}
          placeholder="Enter Quantity Used"
        />
      </div>
      <div className="form-group">
        <label>Production Date:</label>
        <input
          type="date"
          value={productionDate}
          onChange={(e) => setProductionDate(e.target.value)}
        />
      </div>
      <div className="form-buttons">
        <button onClick={handleSave}>{production ? 'Update' : 'Save'}</button>
        <button onClick={onCancel}>Cancel</button>
      </div>
    </div>
  );
};

export default AddEditProductionForm;
