// src/TransactionModal.js

import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { getItemTransactions } from '../layout/inventoryService';
import './TransactionModal.css'; // Create a CSS file for modal styling

Modal.setAppElement('#root'); // For accessibility

const TransactionModal = ({ isOpen, onRequestClose, itemId }) => {
  const [transactions, setTransactions] = useState([]);
  const [inventory, setInventory] = useState({});

  useEffect(() => {
    if (itemId) {
      getItemTransactions(itemId)
        .then(data => {
          setInventory(data.inventory);
          setTransactions(data.transactions);
        })
        .catch(error => console.error('Error fetching transactions:', error));
    }
  }, [itemId, isOpen]);

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Item Transactions"
      className="modal"
      overlayClassName="overlay"
    >
      <div className="modal-header">
        <h2>Transaction Details</h2>
      </div>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Reorder Level</th>
            <th>Total Cost</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(transaction => (
            <tr key={transaction.id}>
              <td>{inventory.name}</td>
              <td>{inventory.reorder_level}</td>
              <td>${(inventory.reorder_level * parseFloat(inventory.unit_price)).toFixed(2)}</td>
              <td>{new Date(transaction.transaction_date).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button type="button" className="close-button" onClick={onRequestClose}>Close</button>
    </Modal>
  );
};

export default TransactionModal;
