// src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './layout/Layout';
import Dashboard from './layout/Dashboard'; // Create this component
import InventoryList from './layout/InventoryList'; // Create this component
import InventoryReport from './layout/InventoryReport'; // Create this component

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route path="dashboard" element={<Dashboard />} />
          <Route path="inventory-list" element={<InventoryList />} />
          <Route path="inventory-report" element={<InventoryReport />} />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;



