/* src/production_layout/production_list/ProductionList.js */

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddEditProductionForm from '../../productionModal/AddEditProductionForm';
import './ProductionList.css';

const API_URL = 'http://localhost:8000/api/productions/'; // Adjust the URL as needed

const ProductionList = () => {
  const [productions, setProductions] = useState([]);
  const [editingProduction, setEditingProduction] = useState(null);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    // Fetch production records on component mount
    const fetchProductions = async () => {
      try {
        const response = await axios.get(API_URL);
        setProductions(response.data);
      } catch (error) {
        console.error('Error fetching productions:', error);
      }
    };

    fetchProductions();
  }, []);

  const handleAddClick = () => {
    setEditingProduction(null); // For adding a new record
    setShowForm(true);
  };

  const handleEditClick = (production) => {
    setEditingProduction(production); // For editing an existing record
    setShowForm(true);
  };

  const handleSave = async (production) => {
    try {
      if (editingProduction) {
        // Update existing record
        await axios.put(`${API_URL}${editingProduction.id}/`, production);
        setProductions(productions.map(p => p.id === production.id ? production : p));
      } else {
        // Add new record
        await axios.post(API_URL, production);
        setProductions([...productions, production]);
      }
    } catch (error) {
      console.error('Error saving production record:', error);
    }
    setShowForm(false);
  };

  const handleCancel = () => {
    setShowForm(false);
  };

  const handleDelete = async (productionID) => {
    if (window.confirm('Are you sure you want to delete this record?')) {
      try {
        await axios.delete(`${API_URL}${productionID}/`);
        setProductions(productions.filter(p => p.id !== productionID));
      } catch (error) {
        console.error('Error deleting production record:', error);
      }
    }
  };

  return (
    <div>
      <h2>Production List</h2>
      <button onClick={handleAddClick}>Add New Production Record</button>
      {showForm && (
        <AddEditProductionForm
          production={editingProduction}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      )}
      <table>
        <thead>
          <tr>
            <th>Production ID</th>
            <th>Product ID</th>
            <th>Raw Material ID</th>
            <th>Quantity Produced</th>
            <th>Quantity Used</th>
            <th>Production Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {productions.map(record => (
            <tr key={record.id}>
              <td>{record.id}</td>
              <td>{record.productID}</td>
              <td>{record.rawMaterialID}</td>
              <td>{record.quantityProduced}</td>
              <td>{record.quantityUsed}</td>
              <td>{record.productionDate}</td>
              <td>
                <button onClick={() => handleEditClick(record)}>Edit</button>
                <button onClick={() => handleDelete(record.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductionList;
