// src/production_layout/productionModal/AddProductionModal.js

import React, { useState } from 'react';
import Modal from 'react-modal';
import './AddProductionModal.css'; // Ensure this path is correct

Modal.setAppElement('#root'); // For accessibility

const AddProductionModal = ({ isOpen, onRequestClose, onAddProduction }) => {
  const [productName, setProductName] = useState('');

  const handleProductEnter = () => {
    // Call the parent component's function to add production
    onAddProduction(productName);
    setProductName(''); // Clear input field
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Add Production"
      className="modal"
      overlayClassName="overlay"
    >
      <div className="modal-header">
        <h2>Add Production</h2>
      </div>
      <form>
        <label>
          Item Name
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                handleProductEnter();
              }
            }}
          />
        </label>
        <button type="button" className="enter-button" onClick={handleProductEnter}>Enter</button>
        <button type="button" className="cancel-button" onClick={onRequestClose}>Close</button>
      </form>
    </Modal>
  );
};

export default AddProductionModal;
