/* src/layout/dashboard/Dashboard.js */

import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [stockLevels, setStockLevels] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/historical-data/');
        const fetchedData = response.data;

        // Aggregate data by date
        const aggregatedData = {};
        fetchedData.forEach(item => {
          const { date, quantity } = item;
          if (!aggregatedData[date]) {
            aggregatedData[date] = {
              date,
              total_quantity: 0
            };
          }
          aggregatedData[date].total_quantity += quantity;
        });

        // Convert aggregated data to array format
        const formattedData = Object.values(aggregatedData);

        setData(formattedData);

        // Set stock levels
        setStockLevels(fetchedData); // Adjust this based on your data structure
      } catch (error) {
        console.error('Error fetching historical data:', error);
      }
    };

    fetchData();
  }, []);

  // Custom tooltip content
  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const { date, total_quantity } = payload[0].payload;
      return (
        <div className="custom-tooltip">
          <p>{`Date: ${date}`}</p>
          <p>{`Total Quantity: ${total_quantity}`}</p>
        </div>
      );
    }

    return null;
  };

  return (
    <div className="dashboard">
      <h2>Inventory Trends Over Time</h2>
      <div className="chart-container">
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip content={<CustomTooltip />} />
            <Legend />
            <Line type="monotone" dataKey="total_quantity" stroke="#8884d8" />
          </LineChart>
        </ResponsiveContainer>
      </div>

      <h3>Stock Levels</h3>
      <table className="stock-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Product</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {stockLevels.map((item, index) => (
            <tr key={index}>
              <td>{item.date}</td>
              <td>{item.product}</td>
              <td>{item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
